# https://adventofcode.com/2022/day/5

stream = """"""
# stream = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"

def getHeader(msg, set_len):
    for header_start in range(len(msg) - set_len):
        if len(set(msg[header_start : header_start+set_len])) == set_len:
            return header_start + set_len
    return -1

print(f"Solution 1: {getHeader(stream,4)}")
print(f"Solution 2: {getHeader(stream,14)}")
